<template>
  <el-header
    height="80px"
    class="flex justify-between items-center"
  >
    <el-tooltip
      content="比較清單"
      placement="top"
    >
      <el-icon size="60">
        <List />
      </el-icon>
    </el-tooltip>
    <SearchBar
      @change="filterName"
    />
    <el-tooltip
      content="前往比較"
      placement="top"
    >
      <el-icon size="60">
        <DArrowRight />
      </el-icon>
    </el-tooltip>
  </el-header>
  <el-main>
    <CardItem
      :span-r-w-d="spanRWD"
      :data="data"
    />
  </el-main>
</template>

<script setup lang='ts'>
import { ref, onMounted, reactive } from 'vue';
import CardItem from './component/CardItem.vue';
import SearchBar from './component/SearchBar.vue';

type DataList = {
  name: string,
  cost: string,
  imgs: Array<{ src: string }>,
}[];

const originData: DataList = [
  {
    name: 'Jacky',
    cost: '20500000',
    imgs: [
      {
        src: 'https://shadow.elemecdn.com/app/element/hamburger.9cf7b091-55e9-11e9-a976-7f4d0b07eef6.png',
      },
      {
        src: 'data:image/jpeg;base64,'
      },
      {
        src: 'data:image/jpeg;base64,'
      },
    ]
  },
  {
    name: 'John',
    cost: '18500000',
    imgs: [
      {
        src: 'https://shadow.elemecdn.com/app/element/hamburger.9cf7b091-55e9-11e9-a976-7f4d0b07eef6.png',
      },
      {
        src: 'https://shadow.elemecdn.com/app/element/hamburger.9cf7b091-55e9-11e9-a976-7f4d0b07eef6.png'
      },
      {
        src: 'https://shadow.elemecdn.com/app/element/hamburger.9cf7b091-55e9-11e9-a976-7f4d0b07eef6.png'
      }
    ]
  },
  {
    name: 'John2',
    cost: '35500000',
    imgs: [
      {
        src: 'https://shadow.elemecdn.com/app/element/hamburger.9cf7b091-55e9-11e9-a976-7f4d0b07eef6.png',
      },
      {
        src: 'https://shadow.elemecdn.com/app/element/hamburger.9cf7b091-55e9-11e9-a976-7f4d0b07eef6.png'
      },
      {
        src: 'https://shadow.elemecdn.com/app/element/hamburger.9cf7b091-55e9-11e9-a976-7f4d0b07eef6.png'
      }
    ]
  },
];

const data: DataList = reactive([]);

const spanRWD = ref<number>(24);

// 偵測視窗大小變化，並重新渲染內容
window.addEventListener('resize', () => {
  spanRWD.value = window.innerWidth < 678 ? 24
  : window.innerWidth > 1000 ? 8 : 12;
});

const filterName = (name: string) => {
  const filteredData = originData.filter((item) => item.name.includes(name));
  const filteredResult = [...filteredData];
  data.splice(0, data.length, ...filteredResult);
};

onMounted(() => {
  spanRWD.value = window.innerWidth < 678 ? 24
  : window.innerWidth > 1000 ? 8 : 12;
  data.splice(0, data.length, ...originData);
});

</script>

